import React from 'react'

import banniere from '../Illustration/Banniere_uniswap.png'
import pack from '../Illustration/pack.png'

import Timer from '../hooks/Timer'

import './Drop.css'

import { useHistory } from 'react-router-dom'


class Drop extends React.Component{

    constructor(props) {
        super(props);
        this.state = {eth_adress: '',mail: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {    this.setState({eth_adress: event.target.eth_adress});this.setState({mail: event.target.mail});  }
      handleSubmit(event) {
        alert(this.state.eth_adress);
        event.preventDefault();
      }

    render() {
        return(
        <div className="drop">
            <img src={banniere} alt="Logo" className="banniere"/>
            <div className="drop-buy">
                <p>Pour participer à un drop, vous devez partager votre adresse ETH public. 
                    Sur cette adresse, vous devez posséder au moins 10 $TRK et les conserver jusqu’au jour du drop. 
                    Vous aurez alors une chance de gagner le drop ci-dessus.</p>
                <img src={pack} alt="Logo"/>
                <div className="drop-form">
                <form>
                    <div className="form-ETH_Mainnet">
                    <label>
                        ETH Mainnet
                        <input type="text" name="ETH_Mainnet" placeholder="0xf7bdD3E931A5431868975E295cA689c9f8e76e01" value={this.state.eth_adress} onChange={this.handleChange}/>
                    </label></div>
                    <div className="form-mail">
                    <label>
                        Mon adresse mail
                        <input type="text" name="mail" value={this.state.mail} onChange={this.handleChange}/>
                    </label>
                    </div>
                    <input type="submit" value="PARTICIPER" name="submit" onClick={this.handleSubmit}/>
                </form>
                </div>
            </div>

        </div>
        )
    }
}

export default Drop;
