import React, {useState} from 'react'
import { useParams } from "react-router-dom";

import boosterHiver2021 from '../Illustration/boosterhiver1.png'
import pDeamon from '../Illustration/partenaire/partenaire-deamon.png'
import pHorimiya from '../Illustration/partenaire/partenaire-horimiya.png'
import pHunter from '../Illustration/partenaire/partenaire-hunter.png'
import pZero from '../Illustration/partenaire/partenaire-zero.png'
import pJujustu from '../Illustration/partenaire/partenaire-jujutsu.png'
import partenaire from '../Illustration/partenaire/partenaire.png'


import './AchatBooster.css'


function AchatBooster(){

    const { name, id } = useParams();
    const [nbBooster, addStateBooster] = useState(0);

    const addBooster = () => {
        addStateBooster(nbBooster + 1)
    };
    const lessBooster = () => {
        if(nbBooster > 1){
            addStateBooster(nbBooster - 1)
        }

    };


    return(
        <div className="Achatbooster">
            <div className="achat">
                <img src={boosterHiver2021} alt="Pack {name}"/>
                <div className="description">
                    <div  style={{paddingBottom:'13px',display:"inline-block",textAlign:'left',float:'left',marginLeft:'6%'}}>
                        <h2 style={{fontSize:'2.5vw'}}>Booster <b>{name} 2021</b></h2>
                        <h2><b>7 Torreko NFTs</b></h2>

                    </div>
                    <div  style={{display:"inline-block",marginTop:'10px',marginLeft:''}}>
                        <p  style={{paddingBottom:'13px',textAlign:'left'}}> Account Balance <br/>10 $TRK</p>
                        <p>PRIX DU BOOSTER 10 $TRK</p>
                    </div>
                    <div style={{width:'',marginTop:'3%'}}>
                        <p  style={{textAlign:'left',left:''}}> LA SAISON</p>
                        <p style={{width:'50vw',float:'left',textAlign:'justify',marginTop:'-1%',color:'white'}}>Hoc inmaturo interitu ipse quoque sui pertaesus excessit e vita aetatis nono 
                            anno atque vicensimo cum quadriennio imperasset. natus apud Tuscos in Massa 
                            Veternensi, patre Constantio Constantini fratre imperatoris, matreque Galla 
                            sorore Rufini et Cerealis</p>
                    </div>
                    <div className="licences">
                        <p  style={{textAlign:'left'}}>Licences:</p>
                        <div style={{width:'60%'}}>
                            <img src={partenaire}/>
                        </div>
                    </div>
                    <div style={{display:"inline-block",width:'80%',fontSize:'20px'}}>
                        <p style={{float:'left'}}>Nombre de Booster(s): </p>
                        <div className='noselect' style={{marginTop:'20px',marginLeft:'10px'}}>
                            <a  onClick={lessBooster} style={{color:'white',backgroundColor:'black',borderRadius:'10px',padding: '5px 15px',marginRight:'10px',userSelect:'none',cursor:'pointer', }}>-</a>
                            <span style={{backgroundColor:'white',color:'red',padding:'5px 25px',borderRadius:'10px'}}>{nbBooster}</span>
                            <span onClick={addBooster} style={{color:'white',backgroundColor:'black',borderRadius:'10px',padding: '5px 15px',marginLeft:'10px',userSelect:'none',cursor:'pointer'}}>+</span>
                            <button style={{backgroundColor:'black',color:'white',borderStyle:'none',fontSize:'20px',padding: '5px 25px',borderRadius:'10px'}} type="submit" value="BUY BOOSTER">BUY BOOSTER</button>
                        </div>

                    </div>
                </div>
            </div>

        </div>


    )
}

export default AchatBooster; 



