import './App.css';

import Home from './Pages/Home'
import BuyBooster from './Pages/BuyBooster'
import AchatBooster from './Pages/AchatBooster'
import MyCollection from './Pages/MyCollection'
import InfoTorekko from './Pages/InfoTorekko'
import OpenCollection from './Pages/OpenCollection'
import Collection from './Pages/Collection'
import Staking from './Pages/Staking/InitAccount'
import Drop from './Pages/Drop'
import MarketPlace from './Pages/MarketPlace'
import Navigation from './Composant/Navigation/Navigation'
import Footer from './Composant/Footer/Footer'
import Erreur404 from './Pages/404'

import test from './Pages/test'
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom'

var Web3 = require('web3');

const providerOptions = {
  portis: {
    package: Portis, // required
    options: {
      id: "8e23465f-c9a7-410a-92df-18b2e3d1c38f",
      network: "maticMumbai"
    }
  }
};

let provider = null;
let web3 = null;
let accounts = null;



function App() {

  var webb = new Web3('https://rinkeby.infura.io/v3/97c1c74d45ee46769f91f51b0a08a6f7')
  var contractAdress = '0xC9BfFbe1a889c9E51fb62d53096B67670e7421d1'
  var abi = require("./Pages/Collectibles_abi.json")
  var contract = new webb.eth.Contract(abi, contractAdress)
  contract.methods.tokenURI(1).call((err, result) => {console.log("res "+result)})


  async function showPortis() {
    if (!provider) {
      const web3Modal = new Web3Modal({
        cacheProvider: true, // optional
        providerOptions // required
      });
      web3 = await connect(web3Modal);
    }

    provider._portis.showPortis();

    if (!accounts) {
      accounts = await web3.eth.getAccounts();
      print(`Wallet address: ${accounts[0].toLowerCase()}`);
    }
  }

  async function connect(web3Modal) {
    provider = await web3Modal.connect();
    return new Web3(provider);
  }

  function print(str) {
    const p = document.createElement("p");
    p.innerText = str;
    document.getElementById("userWalletAddress").appendChild(p);
  }








  return (
    <div className="App">
      <Router
        forceRefresh={true}  
      >
        <Navigation/>
          <Switch>
            <Route path='/' exact component={Home}/>
            <Route path='/BuyBooster' exact component={BuyBooster}/>
            <Route path='/BuyBooster/booster:name/:id' exact component={AchatBooster}/>

            <Route path='/Mycollection' exact component={MyCollection}/>
            <Route path='/Mycollection/:id' exact component={OpenCollection}/>
            <Route path='/Mycollection/Torekko/:id' exact component={InfoTorekko}/>

            <Route path='/Staking' exact component={Staking}/>
            <Route path='/MarketPlace' exact component={MarketPlace}/>
            <Route path='/Drop' exact component={Drop}/>

            <Route path='/Collection/:slug' exact component={Collection}/>
            <Route path='/test' component={test}/>
            <Route path='/' component={Erreur404}/>

          </Switch>
          <Footer/>
      </Router>

    </div>
  );
}

export default App;
