import React,{useEffect} from 'react'
import { NavLink } from 'react-router-dom';
import mainLogo from '../../Illustration/logo-torreko.png'
import './Navigation.css'
import { slide as Menu } from 'react-burger-menu'
import useWindowDimensions from '../../hooks/useWindowDimensions'
import useScrollPosition from '@react-hook/window-scroll'

import BurgerMenu from "./BurgerMenu";





const Navigation = () => {


  var { height, width } = useWindowDimensions();

  function showSettings (event) {
    event.preventDefault();

  }
  const scrollY = useScrollPosition(60 /*fps*/)


      if(width > 810){
        if(scrollY <= 0){
          return(
          <div className="Navigation">
            <nav>
                  <img className="logo" src={mainLogo} alt="Logo" />
                  <div className="menu-plateform">
                    <ul className="liste">
                      <a href="/"><li className="items">Home</li></a>
                      <a href="/BuyBooster"><li className="items">Buy booster</li></a>
                      <a href="/Mycollection"><li className="items">My collection</li></a>
                      <a href="/Staking"><li className="items">Staking</li></a>
                      <a href="/MarketPlace"><li className="items">Marketplace</li></a>
                      <a href="/Drop"><li className="items">Drop</li></a>
                      <li className="items">Blog</li>
                    </ul>
                  </div>
            </nav>
          </div>
          )}
        else{
          return(
            <div className="Navigation">
              <nav>
                    <img className="logo-resize" src={mainLogo} alt="Logo" />
                    <div className="menu-plateform-resize">
                      <ul className="liste-resize">
                        <a href="/"><li className="items">Home</li></a>
                        <a href="/BuyBooster"><li className="items">Buy booster</li></a>
                        <a href="/Mycollection"><li className="items">My collection</li></a>
                        <a href="/Staking"><li className="items">Staking</li></a>
                        <a href="/MarketPlace"><li className="items">Marketplace</li></a>
                        <a href="/Drop"><li className="items">Drop</li></a>
                        <li className="items">Blog</li>
                      </ul>
                    </div>
              </nav>
            </div>
            )}

      }
        else{
          if (scrollY <= 0) {
            return(
              <div class="sticky-thc">
                <img className="logo" src={mainLogo} alt="Logo" />
                <nav>
                    <div className="menu-plateform-resize-mobile">
                      <ul className="liste-resize-mobile">
                        <a href="/"><li className="items">Home</li></a>
                        <a href="/BuyBooster"><li className="items">Buy booster</li></a>
                        <a href="/Mycollection"><li className="items">My collection</li></a>
                        <a href="/Staking"><li className="items">Staking</li></a>
                        <a href="/MarketPlace"><li className="items">Marketplace</li></a>
                        <a href="/Drop"><li className="items">Drop</li></a>
                        <li className="items">Blog</li>
                      </ul>
                    </div>
              </nav>
                </div>
            )
          }
          else{
            return(
              <div class="sticky-thc-resize">
                <nav>
                    <img className="logo-resize" src={mainLogo} alt="Logo" />
                    <div className="menu-plateform-resize-mobile">
                      <ul className="liste-resize-mobile">
                        <a href="/"><li className="items">Home</li></a>
                        <a href="/BuyBooster"><li className="items">Buy booster</li></a>
                        <a href="/Mycollection"><li className="items">My collection</li></a>
                        <a href="/Staking"><li className="items">Staking</li></a>
                        <a href="/MarketPlace"><li className="items">Marketplace</li></a>
                        <a href="/Drop"><li className="items">Drop</li></a>
                        <li className="items">Blog</li>
                      </ul>
                    </div>
              </nav>
              </div>
            )    
          }

        }

}

export default Navigation;
